import React, { Component } from 'react';
import './testQuesJS1.css';
import history from '../history';
// import Result from './Result';
import { NavLink } from 'react-router-dom'


class testQues extends Component {
  state = {
    q1sahi: "",
    q2sahi: "",
    q3sahi: "",
    ans: ["q1sahi", "q2sahi", "q3sahi"],
    val: [],
    result: null
  }

  submit = (event) => {
    let obtain = 0;
    event.preventDefault();
    this.state.ans.map((temp) => {
      this.state.val.map((temp2) => {
        if (temp === temp2) {
          obtain = obtain + 33.33;
        }
        this.setState({
          result: ((obtain) / 100) * 100
        },()=> history.push({
          pathname: "/TestToBeGiven/TestKey/TestQuesJS1/Result",
          state: {percent :this.state.result}
        }))

      })  
    })

  }




  render() {
    console.log("ren", this.state.result)
    return (
      <div>
        <div className="header">
          <header>
            <h3>JavaScript Quiz 1</h3>
          </header>
        </div>
        <form onSubmit={this.submit} >

          <p className="text">1. Inside which HTML element do we put the JavaScript? <br /> <br />
            <input type="radio" className="opt" name="<script/>" value="q1sahi" checked={this.state.q1sahi === 'q1sahi'}
              onChange={(e) => {
                this.setState({
                  q1sahi: e.target.value,
                  val: [...this.state.val, e.target.value]
                })
              }} /> &lt;script/&gt;<br />
            <input type="radio" className="opt" name="q12" value="<js>" checked={this.state.q1sahi === '<js>'}
              onChange={(e) => {
                this.setState({
                  q1sahi: e.target.value,
                  val: [...this.state.val, e.target.value]
                })
              }} />   &lt;js/&gt;<br />
            <input type="radio" className="opt" name="q13" value="<scripting/>" checked={this.state.q1sahi === '<scripting/>'}
              onChange={(e) => {
                this.setState({
                  q1sahi: e.target.value,
                  val: [...this.state.val, e.target.value]
                })
              }} /> &lt;scripting/&gt; <br />
            <input type="radio" className="opt" name="q14" value="<javascript>" checked={this.state.q1sahi === '<javascript>'}
              onChange={(e) => {
                this.setState({
                  q1sahi: e.target.value,
                  val: [...this.state.val, e.target.value]
                })
              }} />&lt;javascript/&gt;<br />
          </p>
          <p className="text">2. What is the correct JavaScript syntax to change the content of the HTML element below?
            <br />
            &lt;p id="demo"&gt;This is a demonstration. &lt;/p&gt;:<br />
            <input type="radio" className="opt" name="2. The word which means fish is" value="ByName" checked={this.state.q2sahi === 'ByName'}
              onChange={(e) => {
                this.setState({
                  q2sahi: e.target.value,
                  val: [...this.state.val, e.target.value]
                })
              }} /> document.getElementByName("p").innerHTML = "Hello World!";<br />
            <input type="radio" className="opt" name="2. The word which means fish is" value="getElement" checked={this.state.q2sahi === 'getElement'}
              onChange={(e) => {
                this.setState({
                  q2sahi: e.target.value,
                  val: [...this.state.val, e.target.value]
                })
              }} /> document.getElement("p").innerHTML = "Hello World!";<br />
            <input type="radio" className="opt" name="getElementById" value="q2sahi" checked={this.state.q2sahi === 'q2sahi'}
              onChange={(e) => {
                this.setState({
                  q2sahi: e.target.value,
                  val: [...this.state.val, e.target.value]
                })
              }} /> document.getElementById("demo").innerHTML = "Hello World!";<br />
          </p>

          <p className="text">3. Where is the correct place to insert a JavaScript?<br />
            <input type="radio" className="opt" name="3. The word which means suitcase is" value="head" checked={this.state.q3sahi === 'head'}
              onChange={(e) => {
                this.setState({
                  q3sahi: e.target.value,
                  val: [...this.state.val, e.target.value]
                })
              }} /> The &lt;head&gt; section <br />
            <input type="radio" className="opt" name="3. The word which means suitcase is" value="head&body" checked={this.state.q3sahi === 'head&body'}
              onChange={(e) => {
                this.setState({
                  q3sahi: e.target.value,
                  val: [...this.state.val, e.target.value]
                })
              }} /> Both the &lt;head&gt; section and the &lt;body&gt; section are correct<br />
            <input type="radio" className="opt" name="body" value="q3sahi" checked={this.state.q3sahi === 'q3sahi'}
              onChange={(e) => {
                this.setState({
                  q3sahi: e.target.value,
                  val: [...this.state.val, e.target.value]
                })
              }} /> The &lt;body&gt; section<br />
          </p>
            <input type="submit" value="Finish"   className="submit" /> 
        </form>
      </div>
    );
  }
}

export default testQues;
